package vignesh.patter;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		int row = 1;
       for (int col=1;col<=row;col++)
       {
    	   System.out.print(row);
    	   
       }
       System.out.println();
       
       int row1 = 2;
       
       for (int col=1;col<=row1;col++)
       {
    	   System.out.print(row1);
    	   
       }
       System.out.println();
       
       int row2 = 3;
       for (int col=1;col<=row2;col++)
       {
    	   System.out.print(row2);
    	   
       }
       System.out.println();
       
       System.out.println();
       
       System.out.println();
       
       
       
       
       
       
       int no= 1;

       

       for(int row4=5;row4>=1;row4--)
    	   
       {
    	   for(int col=1;col<row4;col++)
    	   {
    		   System.out.print(" ");
    		   
       }
    	   for(int col2=row4;col2<=5;col2++)
    	   {
    		   System.out.print(no+ " ");
    		   no++;
    		   
    	   }
    	   System.out.println();
    	   
       }
       }
       
       
	}


